import React, { useState } from 'react'

const MaskTriangle = (props: any) => {
  
  let [mask] = useState(() => {
    switch (props.type) {
      case 'triangle':
        return 'M 0 1, 0.5 0, 0.5 0, 1 1 z'
      case 'square':
        return 'M 0 0, 1 0, 1 1, 0 1 z' 
      default:
        return 'M 0.5, 0.5 m -0.5, 0 a 0.5, 0.5 0 1, 0 1, 0 a 0.5, 0.5 0 1, 0 -1, 0'
    }
  })

  return (
    
    <svg xmlns="http://www.w3.org/2000/svg" 
         xmlnsXlink="http://www.w3.org/1999/xlink" 
         version="1.1" 
         role="img" 
         aria-labelledby="title desc" 
         className="style-scope image-mask card__img mask" 
         viewBox={`0 0 ${props.width} ${props.height}`} 
         width={props.width} 
         height={props.height}
         >
      
      <defs className="style-scope image-mask">
        <clipPath clipPathUnits="objectBoundingBox" className="style-scope image-mask" id="triangle-sxtw">
          <path className="style-scope image-mask" d={mask}></path>
        </clipPath>
      </defs>

      <image height="100%" 
             width="100%" 
             preserveAspectRatio="xMidYMid slice" 
             className="triangle-sxtw style-scope image-mask" 
             clip-path="url(#triangle-sxtw)" 
             xlinkHref={props.img}></image>
      
    </svg>

  
  )
}

export default MaskTriangle;
