import React from 'react'

import { ICardProps } from '../../state/state' 
import style from './card.module.css'
// import '../../assets/styles/index.css'

const Card = ({img, title, text, className}: ICardProps) => {

  const cardStyle = `${style.card} ${className}`
  return (
    <div className={cardStyle}>
      <div className="card__img-wrapper">
        <img className="card__img" src={`${img}`} alt={`${img}`}/>
      </div>

      <div className="card__info">
        <span className={style.card__title}>{title}</span>
        <span className="card__text">{text}</span>
      </div>
    </div>
  )
}

export default Card;
