import React from 'react'

import { ICardProps, IImgWrapStyle } from '../../state/state' 
// import style from './card.module.css'
// import '../../assets/styles/index.css'

import './card.css'

const Card = ({id, img, title, text, className, imgWrapStyle}: ICardProps) => {

  const cardStyle = `card ${className}`;
  const cardImgWrapStyle = ` ${imgWrapStyle}`

  return (
    <div className={cardStyle}>
      <div className="card__inner">
        <div className="card__img-wrapper" style={imgWrapStyle}>
          <span className="card__id">{id.length > 0 && id}</span>
          <img className="card__img" src={`${img}`} alt={`${img}`}/>
        </div>

        <div className="card__info">
          <span className="card__title">{title}</span>
          <span className="card__text">{text}</span>
        </div>
      </div>
    </div>
  )
}

export default Card;
