import React, { useState } from 'react'

import Circle from './circle'

const MaskTriangle = (props: any) => {
  
  let maskType = undefined

  switch (props.type) {
    case 'triangle':
      maskType = 'M 0 1, 0.5 0, 0.5 0, 1 1 z';
      break;
    case 'square':
      maskType = 'M 0 0, 1 0, 1 1, 0 1 z';
      break; 
    default:
      maskType = 'M 0.5, 0.5 m -0.5, 0 a 0.5, 0.5 0 1, 0 1, 0 a 0.5, 0.5 0 1, 0 -1, 0';
      break;
  }
  

  return (
    
      <svg xmlns="http://www.w3.org/2000/svg" 
          xmlnsXlink="http://www.w3.org/1999/xlink" 
          version="1.1" 
          role="img" 
          className="style-scope image-mask card__img mask" 
          viewBox={`0 0 ${props.width} ${props.height}`} 
          width={props.width} 
          height={props.height}
          >
        
        
            {/* <path className="style-scope image-mask" d="M 0.5, 0.5 m -0.5, 0 a 0.5, 0.5 0 1, 0 1, 0 a 0.5, 0.5 0 1, 0 -1, 0"></path>  */}
            {props.type === 'circle' && <Circle />}
          

        <image height="100%" 
              width="100%" 
              preserveAspectRatio="xMidYMid slice" 
              className="mask-name style-scope image-mask" 
              clip-path="url(#mask-name)" 
              xlinkHref={props.img}></image>
        
      </svg>
    

  
  )
}

export default MaskTriangle;
